// Construct progress, failure and confirmation messages
function Message(){this.progress=["Calculating... mehhhh","Baaaahhh... planning sprints...","Optimising velocity via saltlicks..","Grooming baaaacklog..."],this.qnty=Math.floor(Math.random()*20)+2,this.unit=["days","points","weeks","NUTs","gummi goats"],this.response=["You're welcome.","The goat is done goating.","Goat again if you wish.","Baahhhh."],this.failed=["Blast. You didn't rub the goat long and hard enough and your project will overrun catastrophically. Try again","Failed to estimate. Insufficient goat rubbing. Try again?"],this.progressTxt=function(){var e=this.progress[Math.floor(Math.random()*this.progress.length)];return e},this.outcomeTxt=function(){var e=this.qnty+" "+this.unit[Math.floor(Math.random()*this.unit.length)]+". "+this.response[Math.floor(Math.random()*this.response.length)];return e},this.failedTxt=function(){var e=this.failed[Math.floor(Math.random()*this.failed.length)];return e}}function goatConfirm(){rContainer.innerHTML="";rContainer.appendChild(rConfirm(message.outcomeTxt()))}function goatFailed(){rContainer.innerHTML="";rContainer.appendChild(rConfirm(message.failedTxt()))}function goatProgress(){rContainer.innerHTML="";rContainer.appendChild(rConfirm(message.progressTxt()));updateProgress(count);count+=1;return}function updateProgress(e){document.getElementById("progressBar").style.width=e*10+"%"}function resetProgress(){document.getElementById("progressBar").style.width="0%"}function mousedown(){mousedownID===-1&&(mousedownID=setInterval(goatProgress,1e3))}function mouseup(){if(count>=10){clearInterval(mousedownID);mousedownID=-1;resetProgress();goatConfirm()}else{clearInterval(mousedownID);count=0;mousedownID=-1;resetProgress();goatFailed()}}var message=new Message,rContainer=document.getElementById("message"),goatPic=document.getElementById("goatPic"),rConfirm=function(e){var t=document.createTextNode(e);return t},count=0,mousedownID=-1;goatPic.addEventListener("mousedown",mousedown,!1);goatPic.addEventListener("mouseup",mouseup,!1);